mapper.insert:
/*카카오 로그인 일때 null */
INSERT INTO TBL_MEMBER(ID, MEMBER_NAME, MEMBER_LOGIN_CODE, MEMBER_KAKAO_EMAIL, MEMBER_KAKAO_PROFILE_URL) VALUES(SEQ_MEMBER.NEXTVAL, #{memberName}, #{memberLoginCode}, #{memberKakaoEmail}, #{memberKakaoProfileUrl})
INSERT INTO TBL_MEMBER(ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NAME, MEMBER_LOGIN_CODE) VALUES(SEQ_MEMBER.NEXTVAL, #{memberEmail}, #{memberPassword}, #{memberName}, #{memberLoginCode})

mapper.select:
SELECT ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NAME, MEMBER_PROFILE_NAME, MEMBER_PROFILE_PATH, MEMBER_STATUS, MEMBER_LOGIN_CODE, MEMBER_KAKAO_EMAIL, MEMBER_KAKAO_PROFILE_URL
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail} AND MEMBER_PASSWORD = #{memberPassword}

SELECT ID, MEMBER_EMAIL, MEMBER_PASSWORD
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_PASSWORD = #{memberPassword}
WHERE MEMBER_EMAIL = #{memberEmail}

mapper.select:
SELECT ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NAME, MEMBER_PROFILE_NAME, MEMBER_PROFILE_PATH, MEMBER_STATUS, MEMBER_LOGIN_CODE, MEMBER_KAKAO_EMAIL, MEMBER_KAKAO_PROFILE_URL
FROM TBL_MEMBER
WHERE MEMBER_KAKAO_EMAIL = #{memberKakaoEmail}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_KAKAO_PROFILE_URL = #{memberKakaoProfileUrl}
WHERE MEMBER_KAKAO_EMAIL = #{memberKakaoEmail}

UPDATE TBL_MEMBER
SET MEMBER_KAKAO_EMAIL = #{memberKakaoEmail}, MEMBER_KAKAO_PROFILE_URL = #{memberKakaoProfileUrl}, MEMBER_LOGIN_CODE = 'KAKAO'
WHERE ID = #{id}
